						join campaigns on campaigns.id = offers.campaign_id												where campaigns.name = '$partner_name'												and completed = 1												group by date(created_at)" );#### START OUTPUTTING OF DATA AROUND PAYOUT DELIVERY BY MONTH$pages_per_session_query = mysql_query( "select count(*)									from web_traffic_urls wt									join offers on offers.id = wt.offer_id									join campaigns on campaigns.id = offers.campaign_id									where campaigns.name = '$partner_name'");$sessions_overall = 0;$pages_per_session_row = mysql_fetch_array($pages_per_session_query);$pages_per_session = $pages_per_session_row[0];# Go through our payouts by day for the current month, output the data so the user can see it.echo "<BR>";    echo "<h1>" . strtoupper($partner_name) . "<BR><BR></h1>";echo "<table class=\"gridtable\">";echo "<tr>";echo "<th>DATE</th><th>WEB SESSIONS</th><th>PAGE VIEWS</th>";echo "</tr>";$i = 0;                                                while ($row = mysql_fetch_array($payouts_by_day_for_current_month)) {      echo "<tr style=".getbgc($i).">";    echo "<td>" . date("F j, Y", strtotime($row['date'])). " </td><td> " . number_format($row['sessions']) . " </td><td> " . number_format($row['sessions']*$pages_per_session) . "</td>";    echo "</tr>";        $sessions_overall += $row['sessions'];        $i++;                                                    }                                                echo "</table>";                                                echo "<BR/>";echo "<h2>OVERALL STATISTICS: <br><br><FONT COLOR=\"#818181\">" . number_format($sessions_overall) . " sessions<BR>" . number_format($sessions_overall*$pages_per_session) . " total page views <BR></h2><BR></FONT>";echo "<BR/>";?>		<div id="output">	</div>	<script type="text/javascript">var $output = $('#output');var elementOffset = $('#output').offset().top    $(document).ready(function(){  $("#wrapper")	.css("height",elementOffset + "px");});$(function(){  $("table.alternate_color tr:even").addClass("d0");   $("table.alternate_color tr:odd").addClass("d1");});</script>							                                                </div><!----- end press page 1 ----->                            	    	</div>    </div>	<!-- end main content -->		    <div id="footer"><h1>	<font color="#ffffff">CONTACT US<br></font></h1><br>554 5th Avenue<br>6th Floor<br>New York, NY 10036<br>212.692.9500<br><br><a href="mailto:sales@jungroup.com">SALES@JUNGROUP.COM</a>	</div></body></html>
