;		function getbgc($trcount)	{		$blue="\"background-color: #fbfbfb;\"";		$green="\"background-color: #eff0ef;\"";		$odd=$trcount%2;	    if($odd==1){return $blue;}    	else{return $green;    }        }	if (!isset($_SERVER['PHP_AUTH_USER']))	{    $_SERVER['PHP_AUTH_USER'] = 0;	}	$partner_name = $_SERVER['PHP_AUTH_USER'];##################### COLLECTING THE DATA WITH MYSQL ################################ But, for the current month, we're really concerned with showing the payouts by day.$payouts_by_day_for_current_month = mysql_query( "select date(created_at) as date, count(*) as sessions												from viewings												join offers on offers.id = viewings.offer_id												join campaigns on campaigns.id = offers.campaign_id												where campaigns.name = '$partner_name'												and completed = 1												group by date(created_at)" );#### START OUTPUTTING OF DATA AROUND PAYOUT DELIVERY BY MONTH$pages_per_session_query = mysql_query( "select count(*)									from web_traffic_urls wt									join offers on offers.id = wt.offer_id									join campaigns on campaigns.id = offers.campaign_id									where campaigns.name = '$partner_name'");$sessions_overall = 0;$pages_per_session_row = mysql_fetch_array($pages_per_session_query);$pages_per_session = $pages_per_session_row[0];# Go through our payouts by day for the current month, output the data so the user can see it.echo "<BR>";    echo "<h1>" . strtoupper($partner_name) . "<BR><BR></h1>";echo "<table class=\"gridtable\">";echo "<tr>";echo "<th>DATE</th><th>WEB SESSIONS</th><th>PAGE VIEWS</th>";echo "</tr>";                                                while ($row = mysql_fetch_array($payouts_by_day_for_current_month)) {      echo "<tr style=".getbgc($i).">";    echo "<td>" . date("F j, Y", strtotime($row['date'])). " </td><td> " . number_format($row['sessions']) . " </td><td> " . number_format($row['sessions']*$pages_per_session) . "</td>";    echo "</tr>";        $sessions_overall += $row['sessions'];        $i++;                                                    }                                                echo "</table>";                                                echo "<BR/>";echo "<h2>OVERALL STATISTICS: <br><br><FONT COLOR=\"#818181\">" . number_format($sessions_overall) . " sessions<BR>" . number_format($sessions_overall*$pages_per_session) . " total page views <BR></h2><BR></FONT>";echo "<BR/>";?>		<script type="text/javascript">	$(function(){  $("table.alternate_color tr:even").addClass("d0");   $("table.alternate_color tr:odd").addClass("d1");});</script>							                                                </div><!----- end press page 1 ----->                            	    	</div>    </div>	<!-- end main content -->		    <div id="footer"><h1>	<font color="#ffffff">CONTACT US<br></font></h1><br>554 5th Avenue<br>6th Floor<br>New York, NY 10036<br>212.692.9500<br><br><a href="mailto:sales@jungroup.com">SALES@JUNGROUP.COM</a>	</div></body></html>
